import React, { Component } from 'react';
import { propTypes } from 'react-bootstrap/esm/Image';
import { getWardenByEmail,updateWarden } from "../../actions/wardenActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import classnames from "classnames";

 class UpdateWarden extends Component {

    constructor(){
        super();

        this.state = {
            firstName: "",
            wardenid: "",
            mobileNumber: "",
            email:"",
            lastName:"",
            hostelId:"",
            password:""
      
        };
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    componentWillReceiveProps(nextProps){
        const{
            firstName,
            wardenid,
            mobileNumber,
            email,
            lastName,
            hostelId,
            password
        } = nextProps.warden;

       this.setState({
        firstName,
        wardenid,
        mobileNumber,
        email,
        lastName,
        hostelId,
        password

       });

    } 


     componentDidMount(){
         email = localStorage.getItem("")
         const { id } = this.props.match.params;
         this.props.getWardenByEmail(email, this.props.history);
     }

     onChange(e){
         this.setState({[e.target.name]: e.target.value });
     }

     onSubmit(e) {
         e.preventDefault();

         const updateWarden = {

            firstName: this.state.firstName,
            wardenid: this.state.wardenid,
            mobileNumber: this.state.mobileNumber,
            email:this.state.email,
            lastName:this.state.lastName,
            hostelId:this.state.hostelId,
            password:this.state.password
         };
         this.props.updateWarden(updateWarden,this.props.history);
     }
    render() {
        return (
            <div className="project">
            <div className="container">
            <div className="row" style={{width:'800px', margin:'0 auto' ,marginTop:'100px'}}>
                <div className="col-md-8 m-auto">
                    <h5 className="display-4 text-center">Update Warden</h5>
                    <hr />
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <input type="text" className="form-control form-control-lg "
                             placeholder="Warden Id"
                             name="wardenid"
                             value={this.state.wardenid} 
                             onChange={this.onChange}/>
                        </div>
                        <div className="form-group">
                            <input type="text" className="form-control form-control-lg" 
                            placeholder="First Name"
                            name="firstName"
                            value={this.state.firstName}
                            onChange={this.onChange}/>
                        </div>
                        <div className="form-group">
                            <input type="text" className="form-control form-control-lg"
                             placeholder="Last Name"
                             name="lastname"
                             value={this.state.lastName}
                             onChange={this.onChange}/>
                        </div>
                        <div className="form-group">
                        <input type="text" className="form-control form-control-lg"
                         placeholder="Mobile Number"
                         name="mobileNumber"
                         value={this.state.mobileNumber}
                         onChange={this.onChange}/>
                    </div>
                    <div className="form-group">
                    <input type="text" className="form-control form-control-lg"
                     placeholder="Email"
                     name="email"
                     value={this.state.email}
                     onChange={this.onChange}/>
                </div>
                <div className="form-group">
                <input type="text" className="form-control form-control-lg"
                 placeholder="Hostel Id"
                 name="hostelId"
                 value={this.state.hostelId}
                 onChange={this.onChange}/>
            </div>
            <div className="form-group">
            <input type="text" className="form-control form-control-lg" 
            placeholder="Password"
            name="password"
            value={this.state.password}
            onChange={this.onChange}/>
        </div>
                        
                        

                        <input type="submit" className="btn btn-primary btn-block mt-4" />
                    </form>
                </div>
            </div>
        </div>
    </div>
        )
    }
}

UpdateWarden.propTypes = {
    getWardenByEmail: PropTypes.func.isRequired,
    updateWarden:PropTypes.func.isRequired,
    warden: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    warden: state.warden.warden
});

export default connect(
    mapStateToProps,
    {getWardenByEmail,updateWarden} 
    )(UpdateWarden);
